  const line = lines[i];
  // 如果包含 wangenius 并且前面可能是标题
  if (line === 'wangenius' && i > 0) {
    const prevLine = lines[i-1];
    const nextLine = lines[i+1] || '';
    if (prevLine && prevLine.length > 1 && prevLine.length < 50) {
      noteLines.push({
        title: prevLine,
        author: line,
        data: nextLine,
        index: i
      });
    }
  }
}

