input.command:
agent-browser --session xhs_session eval "
// 找到所有笔记链接
const links = Array.from(document.querySelectorAll('a'));
const noteLinks = links.filter(a => {
  const text = a.textContent || '';
  return text.includes('怪奇物语完结38天了') || 
         text.includes('1.31在飞洲国际') ||
         text.includes('怪奇物语5终于来了') ||
         text.includes('你的注意力正在被切割') ||
         text.includes('台球') ||
         text.includes('AI 全家桶') ||
         text.includes('📕');
});

// 获取它们在页面上的位置
const positions = noteLinks.map((link, i) => ({
  text: link.textContent?.trim().substring(0, 30),
  top: link.getBoundingClientRect().top,
  href: link.href
})).sort((a, b) => a.top - b.top);

JSON.stringify(positions.slice(0, 10), null, 2);
"

agent-browser --session xhs_session eval "
const data = {
  url: window.location.href,
  title: document.querySelector('h1')?.textContent?.trim() || 
         document.querySelector('.title')?.textContent?.trim() || 
         document.querySelector('[class*=title]')?.textContent?.trim() || '',
  likes: document.querySelector('[class*=like]')?.textContent?.trim() || '',
  collects: document.querySelector('[class*=collect]')?.textContent?.trim() || '',
  comments: document.querySelector('[class*=comment]')?.textContent?.trim() || '',
  time: document.querySelector('[class*=time]')?.textContent?.trim() || 
        document.querySelector('[class*=date]')?.textContent?.trim() || ''
};

// 也尝试从页面文本中提取
const allText = document.body.innerText;
const lines = allText.split('\\n').map(l => l.trim()).filter(l => l);

// 找到标题行附近的内容
const titleIndex = lines.findIndex(l => l.includes('怪奇物语完结38天'));
if (titleIndex >= 0) {
  data.context = lines.slice(Math.max(0, titleIndex - 2), titleIndex + 5);
}

JSON.stringify(data, null, 2);
"
