    if (contentMatch) {
      console.log('\nContent preview:', contentMatch[1].replace(/<[^>]+>/g, ' ').trim().substring(0, 500));
    }
    console.log('\nStatus:', res.statusCode);
  });
});

req.on('error', (e) => console.error('Error:', e.message));
req.end();
EOF

